mapper.select:
SELECT * FROM ( SELECT doc_seq ,party_cd ,doc_title ,doc_info ,img_src_nm, img_nail_nm, doc_from, doc_url ,doc_stat ,doc_read ,reg_id ,IF(reg_dtm IS NOT NULL, DATE_FORMAT(reg_dtm, '%Y-%m-%d'), '') AS reg_dtm ,IFNULL(show_id, '') ,IF(show_dtm IS NOT NULL, DATE_FORMAT(show_dtm, '%Y-%m-%d'), '') AS show_dtm ,IFNULL(cancel_id, '') ,IF(cancel_dtm IS NOT NULL, DATE_FORMAT(cancel_dtm, '%Y-%m-%d'), '') AS cancel_dtm ,del_id ,IF(del_dtm IS NOT NULL, DATE_FORMAT(del_dtm, '%Y-%m-%d'), '') AS del_dtm from CRBN_MUN_NEWS where party_cd = #{partyCd}
and doc_title like CONCAT('%',#{searchValue},'%')
and doc_info like CONCAT('%',#{searchValue},'%')
ORDER BY doc_seq DESC ) A LIMIT #{pageNo}, #{listSize}

/* 1. ID: selectAdmListCount 2. 업무명: 뉴스 3. 업무설명: 관리자 뉴스 리스트 총갯수 */
SELECT COUNT(1) FROM CRBN_MUN_NEWS where party_cd = #{partyCd}
and doc_title like CONCAT('%',#{searchValue},'%')
and doc_info like CONCAT('%',#{searchValue},'%')

mapper.insert:

/*
    1. ID: insert
    2. 업무명: 뉴스
    3. 업무설명: 뉴스 등록
*/
INSERT INTO CRBN_MUN_NEWS (
    party_cd, doc_title, doc_info, reg_id, img_src_nm, img_nail_nm, doc_from, doc_url
) VALUES (
  #{partyCd}
, #{docTitle}
, #{docInfo}
, #{regId}
, #{imgSrcNm}
 , #{imgNailNm}
 , #{docFrom}
 , #{docUrl}

)

mapper.update:

/*
    1. ID: update
    2. 업무명: 뉴스
    3. 업무설명: 뉴스 변경
*/
UPDATE CRBN_MUN_NEWS
   SET
        doc_title = #{docTitle}
        , img_src_nm = #{imgSrcNm}
        , img_nail_nm = #{imgNailNm}
        , doc_info = #{docInfo}
        , doc_from = #{docFrom}
        , doc_url = #{docUrl}
        ,reg_dtm = now()
  WHERE doc_seq = #{docSeq}
